mapper.select:
select userid, userpw, usernm, useraddr, userzip, userphone, userconfirm, usertype, userpoint
from e_user_tb
where userId = #{userId} and userPw = #{userPw}

mapper.update:
update e_user_tb
set 
 userAddr = #{userAddr},
 userZip = #{userZip},
 userPhone = #{userPhone}
where userId = #{userId}
