mapper.select:
select userid, userpw, usernm, useraddr, userzip, userphone, userconfirm, usertype, userpoint
from e_user_tb
where userId = #{userId} and userPw = #{userPw}

select userid, userpw, usernm, useraddr, userzip, userphone, userconfirm, usertype, userpoint, authkey
from e_user_tb
where userId = #{userId}

mapper.update:
update e_user_tb
set 
 userAddr = #{userAddr},
 userZip = #{userZip},
 userPhone = #{userPhone}
where userId = #{userId}

update e_user_tb
set authkey = #{authkey}
where userId = #{userId}

update e_user_tb
set userConfirm = #{userConfirm}
where userId = #{userId}
